import { NextResponse } from "next/server"
import { sql, safeDbOperation, isNeonAvailable } from "@/lib/neon-db"
import { generateText } from "ai"
import { xai } from "@ai-sdk/xai"
import { randomBytes } from "crypto"
import { structureProfessionalContent } from "@/lib/content-structure" // Declare the variable before using it

export async function POST(request: Request) {
  try {
    // Enhanced request body parsing
    let requestBody
    try {
      const bodyText = await request.text()
      console.log("Content generation request body length:", bodyText.length)

      if (!bodyText || !bodyText.trim()) {
        return NextResponse.json({ error: "Request body is required" }, { status: 400 })
      }

      requestBody = JSON.parse(bodyText)
      console.log("Parsed request body:", JSON.stringify(requestBody, null, 2))

      // Validate required fields
      if (!requestBody.contentType) {
        return NextResponse.json(
          {
            error: "Missing required field: contentType",
          },
          { status: 400 },
        )
      }
    } catch (parseError: any) {
      console.error("JSON parsing error in content generation:", parseError)
      return NextResponse.json(
        {
          error: "Invalid JSON in request body",
          details: parseError.message,
        },
        { status: 400 },
      )
    }

    const { analysisId, contentType, tone = "professional", customPrompt, websiteData } = requestBody

    if (!process.env.XAI_API_KEY) {
      console.error("XAI_API_KEY not found in environment variables")
      return NextResponse.json({ error: "AI service not configured" }, { status: 503 })
    }

    console.log(
      `Generating ${contentType} content with ${tone} tone using Grok for website:`,
      websiteData?.url || "custom prompt",
    )

    // Enhanced AI content generation with Grok
    let aiResponse
    try {
      const prompt = createProfessionalPrompt(contentType, tone, customPrompt, websiteData)
      console.log("Generated prompt length:", prompt.length)

      const result = await generateText({
        model: xai("grok-beta"),
        prompt,
        maxTokens: 4000,
        temperature: 0.3, // Lower temperature for more professional, consistent output
      })

      aiResponse = result.text

      // Validate AI response
      if (!aiResponse || typeof aiResponse !== "string" || aiResponse.trim().length === 0) {
        throw new Error("Empty or invalid AI response from Grok")
      }

      console.log("Grok content generated successfully, length:", aiResponse.length)
    } catch (aiError: any) {
      console.error("Grok AI generation error:", aiError)
      return NextResponse.json(
        {
          error: "Failed to generate content with Grok",
          message: "AI service temporarily unavailable: " + aiError.message,
        },
        { status: 503 },
      )
    }

    // Parse and format the generated content with professional structure
    const contentId = randomBytes(16).toString("hex")
    const structuredContent = structureProfessionalContent(aiResponse.trim(), contentType)

    const generatedContent = {
      id: contentId,
      title: generateProfessionalTitle(contentType, websiteData),
      content: structuredContent.content,
      markdown: structuredContent.markdown,
      summary: structuredContent.summary,
      keyPoints: structuredContent.keyPoints,
      contentType,
      tone,
      analysisId: analysisId || null,
      createdAt: new Date().toISOString(),
      websiteUrl: websiteData?.url || null,
      wordCount: structuredContent.content.split(" ").length,
      readingTime: Math.ceil(structuredContent.content.split(" ").length / 200), // Average reading speed
    }

    // Save to Neon database (only if available and analysisId exists)
    let savedContent = generatedContent
    if (isNeonAvailable() && analysisId) {
      savedContent = await safeDbOperation(
        async () => {
          await sql`
            INSERT INTO generated_content (
              id, analysis_id, content_type, tone, content, markdown, created_at
            ) VALUES (
              ${contentId}, ${analysisId}, ${contentType}, ${tone}, 
              ${generatedContent.content}, ${generatedContent.markdown}, 
              ${generatedContent.createdAt}
            )
          `
          return generatedContent
        },
        generatedContent,
        "Error saving generated content to database",
      )
    }

    console.log("Professional content generation completed successfully")

    return NextResponse.json({
      success: true,
      content: savedContent,
    })
  } catch (error: any) {
    console.error("Content generation error:", error)
    return NextResponse.json(
      {
        error: "Failed to generate content",
        message: error.message || "Unknown error occurred",
      },
      { status: 500 },
    )
  }
}

function generateProfessionalTitle(contentType: string, websiteData?: any): string {
  const websiteName = websiteData?.title || websiteData?.url || "Website"
  const date = new Date().toLocaleDateString("en-US", {
    year: "numeric",
    month: "long",
    day: "numeric",
  })

  const professionalTitles = {
    "sustainability-research": `Comprehensive Sustainability Assessment: ${websiteName}`,
    "scholar-document": `Academic Analysis: Environmental Impact Assessment of ${websiteName}`,
    "mind-map": `Sustainability Framework Analysis: ${websiteName}`,
    "executive-summary": `Executive Brief: Sustainability Performance of ${websiteName}`,
    "technical-audit": `Technical Sustainability Audit Report: ${websiteName}`,
    "improvement-plan": `Strategic Sustainability Improvement Plan: ${websiteName}`,
    "carbon-footprint": `Carbon Footprint Analysis Report: ${websiteName}`,
    "green-hosting": `Green Infrastructure Assessment: ${websiteName}`,
    "performance-report": `Performance & Sustainability Analysis: ${websiteName}`,
    "compliance-check": `Environmental Compliance Assessment: ${websiteName}`,
    "blog-post": `Professional Analysis: ${websiteName}`,
    "social-media": `Social Media Content Strategy: ${websiteName}`,
    newsletter: `Newsletter Feature: ${websiteName}`,
    "press-release": `Press Release: ${websiteName}`,
  }

  return professionalTitles[contentType as keyof typeof professionalTitles] || `Professional Analysis: ${websiteName}`
}

